
public class J1213_11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,k;
		for (i=1 ; i<=7; i++) {                          //北瞀婕
				for (j=7 ; j>i ; j--) {                       //北瞀L钮
					System.out.print(" ");
				}
				for (k =1 ; k <=2*i-1 ; k++) {     //tdL "*"   (2*i-1 @wO膀计)Aヴ蠹骗2A搭@ O膀计
					System.out.print("*");
				}
			System.out.println();
		}
	}

}
